CREATE TABLE `products_pvc_plastic` (
  `id` int(11) NOT NULL COMMENT 'Идентификатор',
  `name` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Наименование',
  `category` int(2) NOT NULL COMMENT 'Категория',
  `subcategory` int(2) NOT NULL COMMENT 'Подкатегория',
  `image` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Изображение',
  `price` int(10) NOT NULL COMMENT 'Цена',
  `opt_price` int(10) NOT NULL COMMENT 'Оптовая цена',
  `pack` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Упаковка',
  `description` varchar(1000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Описание',
  `color` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Цвет',
  `surface` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Поверхность',
  `thickness` float NOT NULL COMMENT 'Толщина',
  `length` int(5) NOT NULL COMMENT 'Длина',
  `width` int(5) NOT NULL COMMENT 'Ширина',
  `manufacturer` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Производитель',
  `description_full` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Полное описание',
  `compound` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Состав',
  `properties` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Свойства',
  `applying` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Применение'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

