-- Дамп данных таблицы `products_pvc_plastic`
--

INSERT INTO `products_pvc_plastic` (`id`, `name`, `category`, `subcategory`, `image`, `price`, `opt_price`, `pack`, `description`, `color`, `surface`, `thickness`, `length`, `width`, `manufacturer`, `description_full`, `compound`, `properties`, `applying`) VALUES
(1, 'ПВХ 3,0мм (1500х3000) RSRigid - NF	', 2, 6, 'pvc-trudnogoruch.jpg', 11633, 10732, 'лист', 'Пластики на основе ПВХ обладают высокой механической прочностью, устойчивостью к химическим веществам и влаге, а также низкой электропроводностью.', '', '', 3, 1500, 3000, 'RSRigid', 'Поливинилхлорид (ПВХ) был одним из первых искусственных материалов, созданных учеными. Его впервые синтезировали в 1835 году, а с 1912 года начались исследования по его промышленному производству. Массовое производство ПВХ стартовало в 1931 году благодаря корпорации BASF. ПВХ относится к категории термопластов.', 'Чистый поливинилхлорид состоит из 43 % этилена, получаемого из нефти, и 57 % связанного хлора, извлекаемого из поваренной соли. Для получения листового пластика и оконных профилей из порошка ПВХ добавляют стабилизаторы, пластификаторы, пигменты и другие вспомогательные добавки.', 'Пластики на основе ПВХ обладают высокой механической прочностью, устойчивостью к химическим веществам и влаге, а также низкой электропроводностью.\r\nОни не растворяются в бензине и керосине, а воздействие кислот и щелочей на них минимально.\r\nКроме того, ПВХ-пластики имеют привлекательный внешний вид и удобны в работе — их легко резать, сваривать и склеивать, а также придавать нужные формы.\r\nЖесткие листовые пластики ПВХ имеют плотность 1,4 г/см³ и характеризуются высокой жесткостью и стойкостью к ударам.\r\nМаксимальная эксплуатационная температура для таких пластиков достигает 700–750 градусов Цельсия, однако при температурах ниже 0 градусов их ударопрочность снижается.\r\nПоверхность листов может быть гладкой, матовой или глянцевой, и они долго сохраняют свои свойства, несмотря на воздействие внешней среды.\r\nВ зависимости от добавок, жесткие листовые пластики могут оставаться неизменными при использовании на открытом воздухе до 15 лет.\r\nНа ровную поверхность листов удобно наносить трафаретную печать и ламинировать их.', 'Жесткий поливинилхлорид широко используется в строительстве для внутренней отделки стен и окон, а также для производства пластиковых сэндвич-панелей.\r\nКорпуса оргтехники также изготавливаются из ПВХ.\r\nВ промышленности он применяется для воздуховодов и емкостей, где требуется высокая инертность к химическим реакциям.\r\nВыставочные стенды, рекламные конструкции и формованные изделия часто производятся из жесткого поливинилхлорида.\r\nЖесткий ПВХ не поддерживает горение, а его листы легко пилятся, фрезеруются и формуются без предварительной сушки.'),
(2, 'ПВХ 4,0мм (2000х3000) RSRigid - NF', 2, 6, 'pvc-trudnogoruch.jpg', 20534, 18942, 'лист', 'Пластики на основе ПВХ обладают высокой механической прочностью, устойчивостью к химическим веществам и влаге, а также низкой электропроводностью.', '', '', 4, 2000, 3000, 'RSRigid', 'Поливинилхлорид (ПВХ) был одним из первых искусственных материалов, созданных учеными. Его впервые синтезировали в 1835 году, а с 1912 года начались исследования по его промышленному производству. Массовое производство ПВХ стартовало в 1931 году благодаря корпорации BASF. ПВХ относится к категории термопластов.', 'Чистый поливинилхлорид состоит из 43 % этилена, получаемого из нефти, и 57 % связанного хлора, извлекаемого из поваренной соли. Для получения листового пластика и оконных профилей из порошка ПВХ добавляют стабилизаторы, пластификаторы, пигменты и другие вспомогательные добавки.', 'Пластики на основе ПВХ обладают высокой механической прочностью, устойчивостью к химическим веществам и влаге, а также низкой электропроводностью.\r\nОни не растворяются в бензине и керосине, а воздействие кислот и щелочей на них минимально.\r\nКроме того, ПВХ-пластики имеют привлекательный внешний вид и удобны в работе — их легко резать, сваривать и склеивать, а также придавать нужные формы.\r\nЖесткие листовые пластики ПВХ имеют плотность 1,4 г/см³ и характеризуются высокой жесткостью и стойкостью к ударам.\r\nМаксимальная эксплуатационная температура для таких пластиков достигает 700–750 градусов Цельсия, однако при температурах ниже 0 градусов их ударопрочность снижается.\r\nПоверхность листов может быть гладкой, матовой или глянцевой, и они долго сохраняют свои свойства, несмотря на воздействие внешней среды.\r\nВ зависимости от добавок, жесткие листовые пластики могут оставаться неизменными при использовании на открытом воздухе до 15 лет.\r\nНа ровную поверхность листов удобно наносить трафаретную печать и ламинировать их.', 'Жесткий поливинилхлорид широко используется в строительстве для внутренней отделки стен и окон, а также для производства пластиковых сэндвич-панелей.\r\nКорпуса оргтехники также изготавливаются из ПВХ.\r\nВ промышленности он применяется для воздуховодов и емкостей, где требуется высокая инертность к химическим реакциям.\r\nВыставочные стенды, рекламные конструкции и формованные изделия часто производятся из жесткого поливинилхлорида.\r\nЖесткий ПВХ не поддерживает горение, а его листы легко пилятся, фрезеруются и формуются без предварительной сушки.'),
(3, 'ПЛАСТИК ПВХ жесткий RSRigid белый. 0,6мм глянцевый (с защитной пленкой)	', 2, 4, 'zhestkiy.png', 1452, 1339, 'лист', 'Пластики на основе ПВХ обладают высокой механической прочностью, устойчивостью к химическим веществам и влаге, а также низкой электропроводностью.', 'белый', 'глянцевый', 0.6, 1500, 3000, 'RSRigid', 'Поливинилхлорид (ПВХ) был одним из первых искусственных материалов, созданных учеными. Его впервые синтезировали в 1835 году, а с 1912 года начались исследования по его промышленному производству. Массовое производство ПВХ стартовало в 1931 году благодаря корпорации BASF.\r\nПВХ относится к категории термопластов.', 'Чистый поливинилхлорид состоит из 43 % этилена, получаемого из нефти, и 57 % связанного хлора, извлекаемого из поваренной соли. Для получения листового пластика и оконных профилей из порошка ПВХ добавляют стабилизаторы, пластификаторы, пигменты и другие вспомогательные добавки.', 'Пластики на основе ПВХ обладают высокой механической прочностью, устойчивостью к химическим веществам и влаге, а также низкой электропроводностью.\r\nОни не растворяются в бензине и керосине, а воздействие кислот и щелочей на них минимально.\r\nКроме того, ПВХ-пластики имеют привлекательный внешний вид и удобны в работе — их легко резать, сваривать и склеивать, а также придавать нужные формы.\r\nЖесткие листовые пластики ПВХ имеют плотность 1,4 г/см³ и характеризуются высокой жесткостью и стойкостью к ударам.\r\nМаксимальная эксплуатационная температура для таких пластиков достигает 700–750 градусов Цельсия, однако при температурах ниже 0 градусов их ударопрочность снижается.\r\nПоверхность листов может быть гладкой, матовой или глянцевой, и они долго сохраняют свои свойства, несмотря на воздействие внешней среды.\r\nВ зависимости от добавок, жесткие листовые пластики могут оставаться неизменными при использовании на открытом воздухе до 15 лет.\r\nНа ровную поверхность листов удобно наносить трафаретную печать и ламинировать их.', 'Жесткий поливинилхлорид широко используется в строительстве для внутренней отделки стен и окон, а также для производства пластиковых сэндвич-панелей.\r\nКорпуса оргтехники также изготавливаются из ПВХ.\r\nВ промышленности он применяется для воздуховодов и емкостей, где требуется высокая инертность к химическим реакциям.\r\nВыставочные стенды, рекламные конструкции и формованные изделия часто производятся из жесткого поливинилхлорида.\r\nЖесткий ПВХ не поддерживает горение, а его листы легко пилятся, фрезеруются и формуются без предварительной сушки.'),
