--
-- Структура таблицы `products_pvc_rod`
--

CREATE TABLE `products_pvc_rod` (
  `id` int(5) NOT NULL COMMENT 'Идентификатор',
  `name` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Наименование',
  `category` varchar(2) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Карегория',
  `subcategory` varchar(2) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Подкатегория',
  `image` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Изображение',
  `price` int(10) NOT NULL COMMENT 'Цена',
  `opt_price` int(10) NOT NULL COMMENT 'Оптовая цена',
  `pack` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Упаковка',
  `description` varchar(1000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Описание',
  `color` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Цвет',
  `diameter` int(5) DEFAULT NULL COMMENT 'Диаметр',
  `applying` varchar(2000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Применение',
  `instruction` varchar(2000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Инструкция'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Дамп данных таблицы `products_pvc_rod`
--

INSERT INTO `products_pvc_rod` (`id`, `name`, `category`, `subcategory`, `image`, `price`, `opt_price`, `pack`, `description`, `color`, `diameter`, `applying`, `instruction`) VALUES
(3, 'Пруток ПВХ для сварки белый d 4 мм', '1', '1', 'bel_1.jpeg', 3887, 3642, 'бухта 5кг', 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов. Сварочный пруток продается бухтами, в 1ой бухте 5кг, примерно 400 м.п.', 'белый', 4, 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов.', '<ul><li>Подготовка рабочей зоны и компонентов. Заточите кончик прутка и прикрепите насадку. Пруток должен выступать из насадки на 45-50 мм.</li>\r\n<li>Нагрев оборудования до необходимой температуры. Предварительный нагрев рабочей поверхности. Для этого используйте обычный строительный фен.</li>\r\n<li>Прямая сварка. Здесь необходимо следить за положением сварочного аппарата. Инструмент наклоняется параллельно шву (канавке). Как только пластик начинает нагреваться, пруток осторожным толкающим движением помещается в шов.</li><br>\r\n<p>При соблюдении правил техники и безопасности сварка пластика не представляет сложности.\r\nСварочный пруток продается бухтами , в 1ой бухте 5кг., примерно 400м.п.</p>'),
(4, 'Пруток ПВХ для сварки серый \"овал 5/3\"', '1', '2', 'ser_oval.jpeg', 4211, 3945, 'бухта 5кг', 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов. Сварочный пруток продается бухтами, в 1ой бухте 5кг, примерно 400 м.п.', 'серый', NULL, 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов.', '<ul><li>Подготовка рабочей зоны и компонентов. Заточите кончик прутка и прикрепите насадку. Пруток должен выступать из насадки на 45-50 мм.</li>\r\n<li>Нагрев оборудования до необходимой температуры. Предварительный нагрев рабочей поверхности. Для этого используйте обычный строительный фен.</li>\r\n<li>Прямая сварка. Здесь необходимо следить за положением сварочного аппарата. Инструмент наклоняется параллельно шву (канавке). Как только пластик начинает нагреваться, пруток осторожным толкающим движением помещается в шов.</li><br>\r\n<p>При соблюдении правил техники и безопасности сварка пластика не представляет сложности.\r\nСварочный пруток продается бухтами , в 1ой бухте 5кг., примерно 400м.п.</p>'),
(5, 'Пруток ПВХ для сварки серый d 3 мм', '1', '2', 'ser_d3.jpeg', 4211, 3945, 'бухта 5кг', 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов. Сварочный пруток продается бухтами, в 1ой бухте 5кг, примерно 400 м.п.', 'серый', 3, 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов.', '<ul><li>Подготовка рабочей зоны и компонентов. Заточите кончик прутка и прикрепите насадку. Пруток должен выступать из насадки на 45-50 мм.</li>\r\n<li>Нагрев оборудования до необходимой температуры. Предварительный нагрев рабочей поверхности. Для этого используйте обычный строительный фен.</li>\r\n<li>Прямая сварка. Здесь необходимо следить за положением сварочного аппарата. Инструмент наклоняется параллельно шву (канавке). Как только пластик начинает нагреваться, пруток осторожным толкающим движением помещается в шов.</li><br>\r\n<p>При соблюдении правил техники и безопасности сварка пластика не представляет сложности.\r\nСварочный пруток продается бухтами , в 1ой бухте 5кг., примерно 400м.п.</p>'),
(6, 'Пруток ПВХ для сварки серый d 4 мм', '1', '2', 'ser_d4.jpg', 4211, 3945, 'бухта 5кг', 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов. Сварочный пруток продается бухтами, в 1ой бухте 5кг, примерно 400 м.п.', 'серый', 4, 'Сварочный пруток применяется для надежной и качественной сварки пластмассовых материалов.', '<ul><li>Подготовка рабочей зоны и компонентов. Заточите кончик прутка и прикрепите насадку. Пруток должен выступать из насадки на 45-50 мм.</li>\r\n<li>Нагрев оборудования до необходимой температуры. Предварительный нагрев рабочей поверхности. Для этого используйте обычный строительный фен.</li>\r\n<li>Прямая сварка. Здесь необходимо следить за положением сварочного аппарата. Инструмент наклоняется параллельно шву (канавке). Как только пластик начинает нагреваться, пруток осторожным толкающим движением помещается в шов.</li><br>\r\n<p>При соблюдении правил техники и безопасности сварка пластика не представляет сложности.\r\nСварочный пруток продается бухтами , в 1ой бухте 5кг., примерно 400м.п.</p>');

