  `price` int(10) NOT NULL,
  `opt_price` int(10) NOT NULL,
  `pack` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` varchar(1000) COLLATE utf8mb4_unicode_ci NOT NULL,
  `surface_1` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `surface_2` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `length` int(10) NOT NULL,
  `width` int(10) NOT NULL,
  `thickness` int(10) NOT NULL COMMENT 'Толщина',
  `description_full` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Полное описание',
  `options` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Основные характеристики',
  `recommendations` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Рекомендации',
  `applying` varchar(3000) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Применение'
